import { decodeBits, decodeMorse } from '.';

describe(decodeMorse.name, () => {
    it('Example from description', () => {
        const bits =
            '1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011';

        expect(decodeMorse(decodeBits(bits))).toBe('HEY JUDE');
    });

    it.each([
        ['1', 'E'],
        ['101', 'I'],
        ['10001', 'EE'],
        ['10111', 'A'],
        ['1110111', 'M'],
    ])('Basic bits decoding: should decode "%s" to "%s"', (bits, characters) =>
        expect(decodeMorse(decodeBits(bits))).toBe(characters),
    );

    it.each([
        ['111', 'E'],
        ['1111111', 'E'],
        ['110011', 'I'],
        ['111000111', 'I'],
        ['111110000011111', 'I'],
        ['11111100111111', 'M'],
        ['111000000000111', 'EE'],
        ['111000111000111', 'S'],
        [
            '',
            'HEY JUDE',
        ],
    ])(
        'Multiple bits per dot handling: should decode "%s" to "%s"',
        (bits, characters) =>
            expect(decodeMorse(decodeBits(bits))).toBe(characters),
    );

    it.each([
        ['01110', 'E'],
        ['000000011100000', 'E'],
    ])('Extra zeros handling: should decode "%s" to "%s"', (bits, characters) =>
        expect(decodeMorse(decodeBits(bits))).toBe(characters),
    );

    it.each([
        [
            '',
            'THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.',
        ],
        [
            '',
            'THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.',
        ],
    ])(
        'Long messages handling: should decode "%s" to "%s"',
        (bits, characters) =>
            expect(decodeMorse(decodeBits(bits))).toBe(characters),
    );

});
